import React from 'react';
import '../../App.css';
import Footer from '../Footer/Footer';
import Cookies from 'js-cookie';

class Team extends React.Component {

  state = {
    //credentials: {username: '', password: ''},
    token: Cookies.get('Token'),
  }

  render(){
    /*
    return(
      <div className="team">{this.state.token}</div>
    )*/
    if (this.state.token) {
      return(
        <div className="team">
          Vous êtes connecté.
          {this.state.token}
        </div>
      )
    }else{
      return (
        <div className="team">
          Vous n'êtes pas connecté, veuillez créer un compte d'abord.
          {this.state.token}
        </div>
      )
    }
    /*
    return (
      <div>
        <div className="team">Gestion d'équipe</div>
        <div className="team">Veuillez vous connecter d'abord</div>
        <Footer/>
      </div>
    )
    */
  }
}
 export default Team;
