import React, { useState, useEffect } from 'react';
import Footer from '../Footer/Footer';

class Contact extends React.Component{
    message = {
        "name": "",
        "email" : "",
        "subject" : "",
    }

    inputChanged = event => {
        const msg = this.message;
        msg[event.target.name] = event.target.value;
        this.setState({message: msg});
      }

    sendMessage() {
        fetch("http://127.0.0.1:8000/api/contact/", {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(this.message),

        })
        .catch( error => console.log(error))
    }


    

    render(){
        return(
            <div className="team">
                <form className="formulaireContact">
                    <label for="fname">Votre nom</label>
                    <input type="text" id="fname" className="formulaireContact" placeholder="Nom" name="name" value={this.message.name} onChange={this.inchanged}></input>

                    <label for="emailField">Votre adresse email</label>
                    <input className="formulaireContact" aria-describedby="email" name="email" id="fmail" placeholder="Email" value={this.message.email} onChange={this.inchanged}></input>

                    <label for="textField">Votre message</label>
                    <input className="formulaireContact" id="subject" name="subject" placeholder="Message..."  value={this.message.subject} onChange={this.inchanged}></input>

                    <button className="button-on" onClick={this.sendMessage}>Enregistrer</button>

                </form>
            
            
            </div>
        )
    }

}

export default Contact;
