import axios from 'axios';
import React, { useState, useEffect } from 'react';
import Footer from '../Footer/Footer';
import './Contact.css';


let csrf_token = "";

class Contact extends React.Component{
    componentDidMount() {
        axios.get("http://127.0.0.1:8000/api/csrf_token/")
        .then(res =>{
            csrf_token = res.data;
        })
    }

    state = {
        credentials: {name: '', email: '', subject: ''},
        alert:'',
        erreur:false,
      
    }
    
      inputChanged = event => {
        const cred = this.state.credentials;
        cred[event.target.name] = event.target.value;
        this.setState({credentials: cred});
      }
    
      
    
    sendMessage() {
        let data = new FormData();
        data.append("name", document.getElementById('name').innerText);
        data.append("email", document.getElementById('email').innerText);
        data.append("subject", document.getElementById('subject').innerText);
        data.append("csrfmiddlewaretoken", csrf_token);
        axios.post("http://127.0.0.1:8000/api/contact/", data)
        .then(res => {
            if(res.data){
                window.location ='/contact';
            }
        })
        .catch(errors => console.log(errors))
    
    }
      
    
      render(){
      return (
          <>
        <div className="team">
          <label className="label-form">
              Nom
              <input type="text" name="name" className="input-form" id="name"
               value={this.state.credentials.name}
               onChange={this.inputChanged} />
            </label>
            <label className="label-form">
              Email
              <input type="email" name="email" className="input-form" id="email"
               value={this.state.credentials.email}
               onChange={this.inputChanged} />
            </label>
            <label className="label-form">
              Message
              <input type="text" name="subject" className="input-form" id="subject"
               value={this.state.subject}
               onChange={this.inputChanged} />
            </label>
            {this.state.alert}
            <button className="button-save" onClick={this.sendMessage} to='/contact'>Envoyer</button>
    
        </div>
        <Footer />
        </>
        )
    }

}

export default Contact;
