import '../../App.css';
import Footer from '../Footer/Footer';
class Flux extends React.Component {
    state = {
        streamer: [
          { nom: "solary" },
          { nom: "ogaminglol" },
          { nom: "gobgg" },
          { nom: "sixentv" },
        ],
        encours: "solary"
      };
  render(){
    return (
      <>
       <ul>
          {this.state.clients.map(client => (
            <li>
            {this.state.nom} 
            </li>
          ))}
        </ul>
    <iframe src="https://player.twitch.tv/?channel=ponce&parent=localhost" frameborder="0" allowfullscreen="true" scrolling="no" height="378" width="620"></iframe>
      </>
    )
  }
}

export default Flux;
