import "../Register/Register.css";
import React, { Component } from 'react';
import '../../App.css';

export default class Add_team extends Component {
    createTeam() {
        newTeam = { id: 0,name: ''}
        fetch('http://127.0.0.1:8000/api/teams/', {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(this.name)
        })
        .then(data => data.json())
        .then(
            data => {
                console.log(data)
            }
        )
        .catch( error => console.error(error))
    }

    render() {
        return (
            <div className='login'>
                <label className='label-form'>
                    Nom de l'équipe
                    <input type="text" name="team_name" className="input-form"></input>
                </label>
                <button className='btn-team' onClick={this.createTeam}>Créer une équipe</button>
            </div>
        )
    }
}
