import axios from 'axios';
import React, { useState, useEffect } from 'react';
import Footer from '../Footer/Footer';
import './Contact.css';


let csrf_token = "";

class Contact extends React.Component{
    componentDidMount() {
        axios.get("http://127.0.0.1:8000/api/csrf_token/")
        .then(res =>{
            csrf_token = res.data;
        })
    }

    state = {
        credentials: {name: '', email: '', subject: ''},
        alert:'',
        erreur:false,
        csrf_token: csrf_token,
      }
    
      inputChanged = event => {
        const cred = this.state.credentials;
        cred[event.target.name] = event.target.value;
        this.setState({credentials: cred});
      }
    
      
    
      sendMessage = event => {
        if(this.state.credentials.name =='' || this.state.credentials.email =='' || this.state.credentials.subject ==''){
          this.setState({alert: <p id='alert'>Veuillez remplir tous les champs</p>})
        }else{
            
            axios.post("http://127.0.0.1:8000/api/contact/", this.state.credentials)
            .then(res => {
                if(res.data){
                    window.location ='/contact';
                }
            })
            .catch(errors => console.log(errors))
        
         }
          
        }
      
    
      render(){
      return (
          <>
        <div className="team">
          <label className="label-form">
              Nom
              <input type="text" name="name" className="input-form" 
               value={this.state.credentials.name}
               onChange={this.inputChanged} />
            </label>
            <label className="label-form">
              Email
              <input type="email" name="email" className="input-form" 
               value={this.state.credentials.email}
               onChange={this.inputChanged} />
            </label>
            <label className="label-form">
              Message
              <input type="text" name="subject" className="input-form" 
               value={this.state.subject}
               onChange={this.inputChanged} />
            </label>
            {this.state.alert}
            <button className="button-save" onClick={this.sendMessage} to='/contact'>Envoyer</button>
    
        </div>
        <Footer />
        </>
        )
    }

}

export default Contact;
