import axios from 'axios';
import React, { useState, useEffect } from 'react';
import Footer from '../Footer/Footer';


class Contact extends React.Component{

    
     
    

    

    sendMessage() {

        let data = new FormData();
        data.append("name", document.getElementById('fname').value);
        data.append("email", document.getElementById('email').value);
        data.append("subject", document.getElementById('subject').value);
        axios.post("http://127.0.0.1:8000/api/contact/", data)
        .then(res => {
            if(res.data){
                windows.location='/contact';
            }
        })
        .catch(errors => console.log(errors))
    }


    

    render(){
        return(
            <div className="team">
                <form className="formulaireContact">
                    <label for="fname">Votre nom</label>
                    <input type="text" id="fname" className="formulaireContact" placeholder="Nom" name="name"></input>

                    <label for="emailField">Votre adresse email</label>
                    <input className="formulaireContact" aria-describedby="email" name="email" id="fmail" placeholder="Email" ></input>

                    <label for="textField">Votre message</label>
                    <input className="formulaireContact" id="subject" name="subject" placeholder="Message..." ></input>

                    <button className="button-on" onClick={this.sendMessage}>Enregistrer</button>

                </form>
            
            
            </div>
        )
    }

}

export default Contact;
