import React from 'react';
import '../../App.css';
import Footer from '../Footer/Footer';
class Flux extends React.Component {
    state = {
        streamer: [
          { nom: "solary" },
          { nom: "ogaminglol" },
          { nom: "gobgg" },
          { nom: "sixentv" },
        ],
        encours: "solary"
      };
    onAdd = streamer => {
        
        this.setState({encours: streamer});
        console.log(streamer);
    };

  render(){
    return (
      <>
       <ul>
          {this.state.streamer.map(streamer => (
            <li>
            {streamer.nom}  <button onClick={() => this.onAdd(streamer.nom)}>X</button>
            </li>
          ))}
        </ul>
        <iframe src="https://player.twitch.tv/?channel=${this.state.encours}&parent=localhost" frameborder="0" allowfullscreen="true" scrolling="no" height="378" width="620"></iframe>
      </>
    )
  }
}

export default Flux;
