import "../Register/Register.css";
import "./Add_team.css";
import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Footer from "../Footer/Footer";
import axios from "axios";

let teams_name = [];
let pseudo = Cookies.get('Pseudo');
let user_id = 0;

export default class Join_team extends Component {

    componentDidMount() {
        axios.get("http://127.0.0.1:8000/api/teams/")
        .then(response =>{
            let data = response.data;
            console.log(data);
            for(let i = 0 ; i < data.length; i++){
                teams_name.push(data[i].name);
            }
            console.log(teams_name);
            
      
        })
        axios.get("http://127.0.0.1:8000/api/users/"+pseudo)
        .then(res => {
            let data = res.data;
            user_id = data.id;
        })

    
        function displayTeams() {
            let list = document.getElementById('listEquipes');
            for(let i = 0; i < teams_name.length; i++){
                let newlistEl = document.createElement("li");
                newlistEl.appendChild(teams_name[i]);
                list.appendChild(newlistEl);


            }
        }
        displayTeams();

    }

    

    render() {
        return (
            <>
                <div className='team'>
                    <div className='contentTeams'>
                        Les Ã©quipes
                    <ul id="listEquipes">
                        

                    </ul>
                    </div>
                </div>
                <Footer/>
            </>
        )
    }
}
