import React from 'react';
import '../../App.css';
import Footer from '../Footer/Footer';
import Cookies from 'js-cookie';
import Offline from '../Offline/Offline'
import axios from 'axios';

const api_Django = 'http://127.0.0.1:8000/api/'//51.210.4.115
let member = String;
class Team extends React.Component {

  //const api_Django = 'http://127.0.0.1:8000/api/'//51.210.4.115
  checkTeam() {
    let team = Object;

    axios.get(api_Django+this.state.pseudo)
    .then(res => {
      console.log(res);
      team = res.team;
      if (team == null){
        member = "Vous n'appartenez pas à une équipe";
        return member;
      }
      else{
        member = "Vous êtes membre de l'équipe: "+ team;
        return member;
      }
    })
    //document.getElementById('team').innerHTML += '<br>'+ member;
  }

  state = {
    pseudo: Cookies.get('Pseudo'),
    token: Cookies.get('Token'),
    team: this.checkTeam,
    membre: member,
  }

  render(){
    if (this.state.token) {
      return(
        <>
          <div className="team" onload="checkTeam()">
            Bienvenue dans la création d'équipe,<br></br>  
            {this.state.pseudo} ! {this.state.team} <br></br>
            {this.state.membre}
          </div>
          <Footer/>
        </>
      )
    }else{
      return (
        <>
          <div className="team" id="team"><Offline/></div>
          <Footer/>
        </>
      )
    }
  }
}
 export default Team;
