import React, { useState } from 'react';
import Add_team from './Add_team';
import '../../App.css';

export default function Not_member() {

    const [active, setActive] = useState("False");

    return (
        <div className='team'>
            <nav>
                <button onClick={() => this.setActive("True")}>Créer une équipe</button>
                <button onClick={() => this.setActive("False")}>Rejoindre une équipe</button>
            </nav>
            <div>
                {active === "True" && <Add_team/>}

            </div>
        </div>
    )
}
