import React, { useState, useEffect } from 'react';
import { Button } from '../Button/Button';
import { Link } from 'react-router-dom';
import './Navbar.css';
import logo from '../../tempfiles/icon.png';
import Cookies from 'js-cookie';
class Navbar extends React.Component{
  state = {
    token: Cookies.get('Token'),
    click: false,
    button: true,
    element: <Link to='/sign-in' id='connexion' className='nav-links-mobile' onClick={this.closeMobileMenu}>Connexion</Link>
  }
  handleClick = () => this.setState({click: !this.state.click});
  closeMobileMenu = () => this.setState({click: false});
  
  showButton = () => {
    if (window.innerWidth <= 960) {
      this.setState({button: false})
    } else {
      this.setState({button: true})
    }
  };
  useEffect = (() => {
    this.showButton();
  }, []);

  render_token = () => {
    if (this.state.token != "undefined") {
      this.setState({element: <Link to='/sign-in' id='connexion' className='nav-links-mobile' onClick={this.closeMobileMenu}>DÃ©connexion</Link>})
    } 
  };
  
  render(){
    window.addEventListener('resize', this.showButton);
    return (
    <>
      <nav className='navbar' onLoad={this.render_token}>
        <div className='navbar-container'>
        <img alt="" src={logo} className="logo"/>
           <Link to='/' className='navbar-title' onClick={this.closeMobileMenu}>
            Azakana
           </Link>
           <div className='menu-icon' onClick={this.handleClick}>
           <i className={this.state.click ? 'fas fa-times' : 'fas fa-bars'} />
           </div>
          <ul className={this.state.click  ? 'nav-menu active' : 'nav-menu'}>

            <li className='nav-item'>
              <Link
                to='/statistiques'
                className='nav-links'
                onClick={this.closeMobileMenu}
              >
                Statistiques
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/team'
                className='nav-links'
                onClick={this.closeMobileMenu}
              >
                Equipe
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/patch-notes'
                className='nav-links'
                onClick={this.closeMobileMenu}
              >
                Notes de patch
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/champions'
                className='nav-links'
                onClick={this.closeMobileMenu}
              >
                Champions
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/e-sport'
                className='nav-links'
                onClick={this.closeMobileMenu}
              >
                E-sport
              </Link>
            </li>
            <li>
              {this.state.element}
            </li>
            
          </ul>
          {this.state.button && <Button buttonStyle='btn--connect'>Connexion</Button>}
        </div>
      </nav>
    </>
  );
}
}

export default Navbar;
