import axios from 'axios';
import React, { Component } from 'react';
import Cookies from 'js-cookie';
import './Member.css';

let csrf_token = "";
let user_id = 0;

export default class Member extends Component {

    state = {
        pseudo: Cookies.get('Pseudo'),
        token: Cookies.get('Token'),
        team: "",
      }

    componentDidMount() {
        axios.get(api_user+this.state.pseudo)
        .then(res => {
            let data = res.data;
            user_id = data.id;
        })
        axios.get("http://127.0.0.1:8000/api/csrf_token/")
        .then(res =>{
            csrf_token = res.data;
        })

    }

    addPlayer() {
        let pseudo = document.getElementById("input").value;
        axios.get("http://127.0.0.1:8000/api/users/"+pseudo)
        .then(res => {
            let data = res.data;
            user_id = data.id;
        })
        let data = new FormData();
        data.append("user_id", user_id);
        data.append("team_name", document.getElementById('span').innerText);
        data.append("csrfmiddlewaretoken", csrf_token);
        axios.post("http://127.0.0.1:8000/api/add_player/", data)
        .then(response => {
            if(response.data){
                console.log("Joueur ajouté");
            }
        })
        .catch(errors => console.log(errors))

    }

    removePlayer(){
        let data = new FormData();
        data.append("user_id", user_id);
        data.append("team_name", this.props.team);
        data.append("csrfmiddlewaretoke", csrf_token);
        axios.put("http://127.0.0.1:8000/api/members/", data)
        .then(response => {
            if(response.data){
                console.log("Joueur retiré");
            }
        })
        .catch(errors => console.log(errors))

    }


   

    render() {
        return (
            <div className='team'>
                <div className='content'>
                <div id="teamname">
                    Equipe <span id="span">{this.props.team}</span>
                </div>
                
                <div id="teamChangerContent">
                    <input id="input" type="text" name="player"  placeholder="Nom du joueur"></input>
                    <br></br>
                    <button id="addPlayer"  onClick={this.addPlayer}>Ajouter joueur</button>
                    <button id="removePlayer" onClick={this.removePlayer}>Retirer joueur</button>
                <div id="message">
                    
                </div>
                </div>
                </div>
            </div>
        )
    }
}
