import React from 'react';
import '../../App.css';
import Footer from '../Footer/Footer';
class Flux extends React.Component {
    state = {
        streamer: [
          { url: 'https://player.twitch.tv/?channel=solary&parent=localhost',nom: solary },
          { url: 'https://player.twitch.tv/?channel=ogaminglol&parent=localhost',nom: ogaminglol },
          { url: 'https://player.twitch.tv/?channel=gobgg&parent=localhost',nom: gobgg },
          { url: 'https://player.twitch.tv/?channel=sixentv&parent=localhost',nom: sixentv },
        ],
        encours: "https://player.twitch.tv/?channel=solary&parent=localhost"
      };
    onAdd = streamer => {
        
        this.setState({encours: streamer});
    };

  render(){
    return (
      <>
       <ul>
          {this.state.streamer.map(streamer => (
            <li>
            {streamer.nom}  <button onClick={() => this.onAdd(streamer.nom)}>X</button>
            </li>
          ))}
        </ul>
        <iframe src={this.state.encours} frameborder="0" allowfullscreen="true" scrolling="no" height="378" width="620"></iframe>
      </>
    )
  }
}

export default Flux;
